def function(sequence_1: str,
             sequence_2: str) \
        -> int:
    difference = 0
    for base_1, base_2 in zip(sequence_1, sequence_2):
        if base_1 != base_2:
            difference += 1

    return difference


if __name__ == '__main__':
    p_1 = ""
    p_2 = ""
    print(function(sequence_1=p_1, sequence_2=p_2))
